  Player Age: {{ $json.body.age }}
  Favorite Genres: {{ $json.body.genres.join(", ") }}
  Genre 1 Preferences: {{ $json.body.genre1Preferences }}
  Genre 2 Preferences: {{ $json.body.genre2Preferences }}
