parameters.jsCode:
// Parse the AI response to get songs list
const response = $input.item.json;

console.log('Received data:', JSON.stringify(response, null, 2));

// If the response is directly an array, use it
let songs = Array.isArray(response) ? response : response.output || response.text;

// If still not an array, try parsing as string
if (typeof songs === 'string') {
  songs = JSON.parse(songs);
}

// Final validation
if (!Array.isArray(songs)) {
  throw new Error(`Expected array but got: ${typeof songs}`);
}

console.log(`Processing ${songs.length} songs for quiz mode`);

// Return each song for parallel processing
return songs.map((song, index) => ({
  json: {
    artist: song.artist,
    song: song.song,
    searchQuery: `${song.song} ${song.artist}`,
    index: index
  }
}));

// Parse wrong answers and combine with song data
const wrongAnswersResponse = $input.item.json;
const songData = $node['Parse Song List'].item.json;

console.log('Processing wrong answers for:', songData.song);

// Parse wrong answers
let wrongAnswers = wrongAnswersResponse;

if (typeof wrongAnswers === 'string') {
  wrongAnswers = JSON.parse(wrongAnswers);
} else if (wrongAnswers.output) {
  wrongAnswers = typeof wrongAnswers.output === 'string' 
    ? JSON.parse(wrongAnswers.output) 
    : wrongAnswers.output;
} else if (wrongAnswers.text) {
  wrongAnswers = typeof wrongAnswers.text === 'string' 
    ? JSON.parse(wrongAnswers.text) 
    : wrongAnswers.text;
}

// Ensure we have an array of exactly 3 wrong answers
if (!Array.isArray(wrongAnswers) || wrongAnswers.length < 3) {
  console.warn('Invalid wrong answers, using fallback');
  wrongAnswers = [
    `Unknown Artist 1 - Song ${songData.index + 1}A`,
    `Unknown Artist 2 - Song ${songData.index + 1}B`,
    `Unknown Artist 3 - Song ${songData.index + 1}C`
  ];
}

wrongAnswers = wrongAnswers.slice(0, 3);

console.log('Wrong answers:', wrongAnswers);

return [{
  json: {
    ...songData,
    wrongAnswers: wrongAnswers
  }
}];

// Combine Spotify data with wrong answers
const spotifyTrack = $input.item.json;
const songDataWithWrongAnswers = $node['Combine Song + Wrong Answers'].item.json;

console.log('Combining Spotify track with wrong answers');

if (!spotifyTrack || !spotifyTrack.uri || !spotifyTrack.id) {
  console.error('Invalid Spotify track data');
  return [];
}

const result = {
  uri: spotifyTrack.uri,
  id: spotifyTrack.id,
  title: spotifyTrack.name,
  artist: spotifyTrack.artists?.[0]?.name || 'Unknown',
  wrongAnswers: songDataWithWrongAnswers.wrongAnswers,
  index: songDataWithWrongAnswers.index
};

console.log('Final song data:', result);

return [{ json: result }];

// Aggregate all songs with their wrong answers
const allItems = $input.all();
const trackUris = [];
const songsData = [];

console.log('Aggregating', allItems.length, 'songs with wrong answers');

for (const item of allItems) {
  const song = item.json;

  if (song && song.uri && song.id) {
    trackUris.push(song.uri);
    songsData.push({
      uri: song.uri,
      title: song.title,
      artist: song.artist,
      wrongAnswers: song.wrongAnswers
    });

    console.log('Song:', song.title, 'by', song.artist);
    console.log('  Wrong answers:', song.wrongAnswers);
  }
}

console.log('Total tracks:', trackUris.length);

if (trackUris.length === 0) {
  throw new Error('No valid tracks found');
}

const trackUrisString = trackUris.join(',');

return [{
  json: {
    trackUris: trackUris,
    trackUrisString: trackUrisString,
    songs: songsData,
    totalSongs: songsData.length
  }
}];
